package Examples;

public class SwitchPractice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// We have 1 machine and 3 buttons on that coffee machine ,each button gives
		// different coffee
		// Button 1-Black tea
		// Button 2-Orange pekoe
		// Button 3-hot chocolate
		int button = 2;
		switch (button) {
		case 1:
			// Button1
			System.out.println("Pour Black tea");
			break;
		case 2:
			// Button2
			System.out.println("Pour orange pekoe");
			break;
		case 3:
			// Button3
			System.out.println("Pour hot chocolate");
		}
				}

}
